import React from "react";
import axios from "axios";
import { useForm } from "react-hook-form";

export function StudentForm({ preloadedData }) {
  const { register, handleSubmit, reset } = useForm({
    defaultValues: preloadedData,
  });

  const onSubmit = async (data) => {
    // If there's no current student, add new
    if (!preloadedData) {
      axios
        .post(`/api/students`, data)
        .then((response) => {
          console.log("Add:", data);
          console.log(response.status, response.data);
          reset();
        })
        .catch((error) => {
          console.error(error);
        });
      // Else, update current one
    } else {
      axios
        .put(`/api/student/${preloadedData.id}`, data)
        .then((response) => {
          console.log("Update:", data);
          console.log(response.status, response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="">
        <label>
          Full Name
          <input
            name="fullName"
            {...register("fullName", {
              required: true,
              pattern: /^[A-Za-z]+(\s[A-Za-z]+)+$/i,
            })}
          />
        </label>
      </div>

      <div className="">
        <label>
          Grade
          <select {...register("grade")}>
            {Array.from({ length: 12 }, (_, i) => i + 1).map((value) => (
              <option key={value} value={value}>
                {value}
              </option>
            ))}
          </select>
        </label>
      </div>

      <div className="">
        <label>
          Parent Name
          <input
            name="parentName"
            {...register("parentName", {
              required: true,
              pattern: /^[A-Za-z]+(\s[A-Za-z]+)+$/i,
            })}
          />
        </label>
      </div>

      <div className="">
        <label>
          Address
          <input
            name="address"
            {...register("address", {
              required: true,
              minLength: 10,
              maxLength: 20,
            })}
          />
        </label>
      </div>

      <div className="">
        <label>
          Phone Number
          <input
            name="phone"
            {...register("phone", {
              required: true,
              minLength: 10,
              maxLength: 20,
              pattern: /^[0-9]+$/,
            })}
          />
        </label>
      </div>

      <div className="">
        <label>
          Email
          <input
            type="email"
            name="email"
            {...register("email", { required: true })}
          />
        </label>
      </div>

      <button type="submit">Submit</button>
    </form>
  );
}
